data = ""

local seen_chars = {} -- Use as a dictionary
local counter = 0
local i = 1

while(i < string.len(data))
do
    local c = data:sub(i,i) -- Get individual char
    if (seen_chars[c]) -- Repeated char, clear dictionary and reset index to that last repeated char
    then
        i = seen_chars[c]
        seen_chars = {}
        counter = 0
    else
        seen_chars[c] = i
        counter = counter + 1
    end

    if (counter == 4)
    then
        print(i)
        break
    end
    i = i + 1
end